<script>
    import * as anime from 'animejs'
    import Anique from '../../assets/js/anique'

    import {
        animateItemsFromSpot,
        decryptLetters,
        showTextWithBg,
        removeWavesAnimations,
        encryptLetters, hideTextWithBg, staticTranslate
    } from "../../assets/js/animateFunctions";
    import {Parallax} from "../../assets/js/parallax";

    export default {
        name: "Section2",
        data: () => ({
            locale: {
                ru: {
                    line1: 'Преимущества',
                    line2: 'Глубокая предпроектная работа, бизнес-аналитика и проектирование;',
                    line3: 'Гибкая и поэтапная разработка, экономим ваше время и деньги;',
                    line4: 'Эмоциональный дизайн – транслируем нужную эмоцию вашим пользователям',
                    line5: 'Гибкий бюджет - планируем бюджет целиком или этапами;',
                    line6: 'Подбираем методологию разработки индивидуально под проект',
                    buffer: {
                        line1: 'Преимущества',
                        line2: 'Глубокая предпроектная работа, бизнес-аналитика и проектирование;',
                        line3: 'Гибкая и поэтапная разработка, экономим ваше время и деньги;',
                        line4: 'Эмоциональный дизайн – транслируем нужную эмоцию вашим пользователям',
                        line5: 'Гибкий бюджет - планируем бюджет целиком или этапами;',
                        line6: 'Подбираем методологию разработки индивидуально под проект',
                    }
                },
                en: {
                    line1: 'Advantages',
                    line2: 'Deep pre-project working, business analytics and planning;',
                    line3: 'Flexible and staged development, saving your time and finances;',
                    line4: 'Emotional design - transfering the right emotion to your customers;',
                    line5: 'Full or staged budget planning;',
                    line6: 'Choosing the individual development technique to each project.',
                    buffer: {
                        line1: 'Advantages',
                        line2: 'Deep pre-project working, business analytics and planning;',
                        line3: 'Flexible and staged development, saving your time and finances;',
                        line4: 'Emotional design - transfering the right emotion to your customers;',
                        line5: 'Full or staged budget planning;',
                        line6: 'Choosing the individual development technique to each project.',
                    }
                }
            }
        }),
        computed: {
            demount(){
                return this.$store.state.appState.animationQueue.startDemountCurrentSlide
            },
            isSecondSlide() {
                return this.$store.state.appState.slides[1]
            },
            isRouteWipe() {
                return this.$store.state.startDestructionOnRouteLeave;
            },
            lang() {
                return this.$store.state.appState.language;
            }
        },
        watch: {
            demount(newValue, oldValue ) {

                if (oldValue != newValue && newValue == true && this.isSecondSlide) {
                        this.hideAnimObj.start(this.$store.getters.getMovingType);

                        document.querySelector('#section2').classList.remove('section-2-optimize');
                        setTimeout(()=>{
                            this.hideAnimObj.destroy();
                            this.showAnimObj.destroy();
                            this.$store.commit('toggleSlides',{fromId: 2, toId : this.$store.state.appState.animationQueue.nextSlide})
                        },2800);
                }
            },
            isSecondSlide(newValue, oldValue){
                if (oldValue != newValue && newValue == true) {
                    document.querySelector('#section2').classList.add('section-2-optimize');
                    this.$store.state.imageEffect.fillCorrectors(1);
                    this.$store.state.imageEffect.fillPositions(1);
                    this.$store.state.imageEffect.renderSceneOnce(1);
                    this.showAnimObj.start(this.$store.getters.getMovingType);
                }
            },
            isRouteWipe(newValue, oldValue) {
                if (oldValue != newValue && newValue == true) {
                    this.hideAnimObj.destroy();
                    this.showAnimObj.destroy();
                }
            },
            lang(newValue, oldValue) {
                if (oldValue != newValue && this.isSecondSlide) {
                    let title = document.querySelectorAll('#section2 .lang-decrypt');
                    let static_translate = document.querySelectorAll('#section2 .lang-static');
                    setTimeout(()=>{
                        Array.from(title).forEach((el, i) => {
                            decryptLetters(el, 300)
                        });
                        Array.from(static_translate).forEach(el => {
                            staticTranslate(el)
                        });
                    }, 50)


                }
            }
        },
        beforeDestroy(){

        },
        mounted() {
            let title = document.querySelector('.section-2__title span');
            this.waves = document.querySelector('.section-2__waves');
            let zeus = document.querySelector('.section-2__zeus--animation');
            let videoWrapper = document.querySelector('.section__video-wrapper');
            let canvasWrapper = document.querySelector('.section__video-canvas');
            let tgMoving = {
                value: 0
            };

            this.showAnimObj = new Anique()
                .add({
                    name: 'move bg',
                    disabled:false,
                    call: () => {
                        let t3d = {
                            value: 0
                        };
                        let webgl = document.querySelector('.webgl-backgrounds');
                        anime({
                            targets: t3d,
                            value: [100, 0],
                            duration: 1000,
                            elasticity: 0,
                            easing: [0.790, 0.010, 0.655, 0.990],
                            offset: 0,
                            update() {
                                webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                            },
                            complete: () => {
                                videoWrapper.style.display = "block";
                                canvasWrapper.style.display = 'none';
                                this.$store.state.zoomEffect.stopAnimate();
                            }
                        });
                    },
                    alternateCall: () => {},
                    delay:1000,
                    next: [
                        {
                           name: 'holes',
                           call: ()=>{
                               this.$store.state.imageEffect.animateHoles(1, 'show');
                           }
                        }, {
                            name: 'decrypt letters',
                            call: ()=>{
                              anime({
                                  targets: '.section-2__title',
                                  opacity: [0,1],
                                  duration: 800,
                                  elasticity: 0,
                                  easing: 'easeOutSine',
                                  offset: 0
                              });
                                decryptLetters(title, 800);
                            }
                        }, {
                            name: 'zeus',
                            disabled:false,
                            call: () => {
                                let tg = {
                                    value: 0,
                                    opacity: 0
                                };
                                anime({
                                    targets: tg,
                                    opacity: [0, 1],
                                    value: [100, 0],
                                    offset: 0,
                                    easing: 'easeOutSine',
                                    duration: 600,
                                    elasticity: 0,
                                    update() {
                                        zeus.style.transform='translate3d(0px,' + tg.value + 'px,0px)';
                                        zeus.style.opacity=tg.opacity;
                                    }
                                })
                            },
                            delay: 600,
                            next: [
                                {
                                    name: 'set zeus moving',
                                    call: () => {


                                        anime({
                                            targets: tgMoving,
                                            value: 40,
                                            duration: 1800,
                                            easing: 'easeInOutSine',
                                            elasticity: 0,
                                            loop: true,
                                            direction: 'alternate',
                                            update() {
                                                zeus.style.transform='translate3d(0px,' + tgMoving.value + 'px,0px)';
                                            }
                                        });
                                    },
                                    purge: () => {
                                        anime.remove(tgMoving);
                                        tgMoving.value = 0;
                                    }

                                }, {
                                    name: 'set waves',
                                    disabled: false,
                                    call: () => {
                                        this.waves.classList.add('waves-animation');
                                    },
                                    purge: () => {
                                        removeWavesAnimations.call(this.waves);
                                    }
                                }, {
                                    name: 'set small line',
                                    call: () => {
                                        anime({
                                            targets: '.section-2-small-spot__line',
                                            scale: [0, 1],
                                            rotate: [-45, -45],
                                            duration: 400,
                                            elasticity: 0,
                                            easing: 'easeOutSine',
                                            offset: 0
                                        });
                                    }
                                }, {
                                    name: 'form',
                                    offset: 1200,
                                    call: () => {
                                        anime({
                                            targets: '.section-2 .section-line-form',
                                            opacity: [0, 1],
                                            duration: 400,
                                            elasticity: 0,
                                            easing: 'easeOutSine',
                                            offset: 0
                                        })
                                    }
                                }
                            ]
                        }, {
                            name: 'text',
                            offset: 400,
                            call: () => {
                                showTextWithBg('.section-2')
                            },
                            delay: 1200,
                            next: [
                                {
                                    name: 'set stop animation flag',
                                    call: () => {
                                        this.$store.commit('appStateMutate', {
                                            animationIsActive: false
                                        });
                                    }
                                }, {
                                    name: 'set parallax',
                                    disabled:false,
                                    call: () => {
                                        this.parallax = new Parallax(
                                            document.querySelector('.section-2'),
                                            false
                                        );
                                    },
                                    purge: () => {
                                        this.parallax.destroy();
                                        this.parallax = null;
                                    }
                                }, {
                                    name: 'set throwing objects',
                                    disabled: false,
                                    call: () => {
                                        this.spotItemsTimer = new animateItemsFromSpot('.section-2__spot');
                                    },
                                    purge: () => {
                                        this.spotItemsTimer.destroy();
                                        this.spotItemsTimer = null;
                                    }
                                }, {
                                    name: 'unlock navigation',
                                    call: () => {
                                        this.$store.commit('unlockNav')
                                    }
                                }
                            ]
                        }
                    ]
                });

            this.hideAnimObj = new Anique()
                .add({
                    name: 'hiding waves',
                    call: () => {
                        this.waves.classList.add('waves-hide');
                    }
                })
                .add({
                    name: 'zeus',
                    call: () => {
                        let tg = {
                            value: 0,
                            opacity: 0
                        };
                        anime({
                            targets: tg,
                            opacity: [1, 0],
                            value: [0, 100],
                            offset: 0,
                            easing: 'easeOutSine',
                            duration: 600,
                            elasticity: 0,
                            update() {
                                zeus.style.transform='translate3d(0px,' + tg.value + 'px,0px)';
                                zeus.style.opacity = tg.opacity;
                            },
                            complete() {
                                zeus.style.transform= 'translate3d(0,0,0)';
                            }
                        })
                    },
                    delay: 400,
                    next: [{
                            name: 'hide holes',
                            call: () => {
                                this.$store.state.imageEffect.animateHoles(1, 'hide');
                            }
                        }, {
                            name: 'small line',
                            call: () => {
                                anime({
                                    targets: '.section-2-small-spot__line',
                                    scale: [1, 0],
                                    rotate: [-45, -45],
                                    duration: 400,
                                    elasticity: 0,
                                    easing: 'easeOutSine'
                                })
                            },
                            delay: 400,
                            next: [
                                {
                                    name: 'more lines',
                                    call: () => {
                                        anime({
                                            targets: '.section-2 .section-line-form',
                                            opacity: [1, 0],
                                            duration: 400,
                                            elasticity: 0,
                                            easing: 'easeOutSine'
                                        })
                                    },
                                    delay: 400,
                                    next: [
                                        {
                                            name: 'move bg',
                                            call: () => {
                                                videoWrapper.style.display = "none";
                                                canvasWrapper.style.display = 'block';
                                                this.$store.state.zoomEffect.animate();
                                                let t3d = {
                                                    value: 0
                                                };
                                                let webgl = document.querySelector('.webgl-backgrounds');
                                                anime({
                                                    targets: t3d,
                                                    value: [0, 100],
                                                    duration: 1000,
                                                    elasticity: 0,
                                                    easing: [0.790, 0.010, 0.655, 0.990],
                                                    offset: 0,
                                                    update() {
                                                        webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                                                    }
                                                });
                                            },
                                            alternateCall: () => {
                                                this.$store.state.imageEffect.animateNext(1, this.$store.getters.getNextSlide-1, 2000);
                                            }
                                        }
                                    ]
                                }, {
