                        anime({
                            targets: t3d,
                            value: [100, 0],
                            duration: 1000,
                            elasticity: 0,
                            easing: [0.790, 0.010, 0.655, 0.990],
                            offset: 0,
                            update() {
                                webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                            },
                            complete: () => {
                                videoWrapper.style.display = "block";
                                canvasWrapper.style.display = 'none';
                                this.$store.state.zoomEffect.stopAnimate();
                            }
                        });
                    },
                    alternateCall: () => {},
                    delay:1000,
                    next: [
                        {
                            name: 'holes',
                            call: ()=>{
                                this.$store.state.imageEffect.animateHoles(2, 'show');
                            }
                        }, {
                            name: 'set waves',
                            call: () => {
                                this.waves.classList.add('waves-animation');
                            },
                            purge: () => {
                                removeWavesAnimations.call(this.waves);
                            }
                        }, {
                            name: 'decrypt letters',
                            call: () => {
                              anime({
                                  targets: '.section-3__title',
                                  opacity: [0,1],
                                  duration: 800,
                                  elasticity: 0,
                                  easing: 'easeOutSine',
                                  offset: 0
                              });
                                decryptLetters(title, 800);
                            },
                            delay: 700,
                            next: [
                                {
                                    name: 'show hands',
                                    call: () => {
                                        let tg = {
                                            value: 0,
                                            opacity: 0
                                        };
                                        anime({
                                            targets: tg,
                                            opacity: [0, 1],
                                            value: [100, 0],
                                            offset: 0,
                                            easing: 'easeOutSine',
                                            duration: 600,
                                            elasticity: 0,
                                            update() {
                                                hands.style.transform='translate3d(0px,' + tg.value + 'px,0px)';
                                                hands.style.opacity=tg.opacity;
                                            }
                                        })
                                    },
                                    delay: 600,
                                    next: [
                                        {
                                            name: 'set small line',
                                            call: () => {
                                                anime({
                                                    targets: '.section-3-small-spot__line',
                                                    scale: [0, 1],
                                                    rotate: [-45, -45],
                                                    duration: 400,
                                                    elasticity: 0,
                                                    easing: 'easeOutSine',
                                                    offset: 0
                                                })
                                            }
                                        }, {
                                            name: 'set form',
                                            call: () => {
                                                anime({
                                                    targets: '.section-3 .section-line-form',
                                                    opacity: [0, 1],
                                                    duration: 400,
                                                    elasticity: 0,
                                                    easing: 'easeOutSine',
                                                    offset: 0
                                                })
                                            }
                                        }, {
                                            name: 'set subtitle',
                                            call: () => {
                                                anime({
                                                    targets: '.section-3__subtitle',
                                                    opacity: [0, 1],
                                                    duration: 600,
                                                    elasticity: 0,
                                                    easing: 'easeOutSine',
                                                    offset: 0
                                                })
                                            },
                                            delay: 200,
                                            next: [
                                                {
                                                    name: 'set text',
                                                    call: () => {
                                                        showTextWithBg('.section-3')
                                                    },
                                                    delay: 1200,
                                                    next: [
                                                        {
                                                            name: 'stop animation',
                                                            call: () => {
                                                                this.$store.commit('appStateMutate', {
                                                                    animationIsActive: false
                                                                });
                                                            }
                                                        }, {
                                                            name: 'install parallax',
                                                            call: () => {
                                                                this.parallax = new Parallax(
                                                                    document.querySelector('.section-3'),
                                                                    false
                                                                );
                                                            },
                                                            purge: () => {
                                                                this.parallax.destroy();
                                                                this.parallax = null;
                                                            }
                                                        }, {
                                                            name: 'set throwing objects',
                                                            disabled: false,
                                                            call: () => {
                                                                this.spotItemsTimer = new animateItemsFromSpot('.section-3__spot');
                                                            },
                                                            purge: () => {
                                                                this.spotItemsTimer.destroy();
                                                                this.spotItemsTimer = null;
                                                            }
                                                        }, {
                                                            name: 'unlock navigation',
                                                            call: () => {
                                                                this.$store.commit('unlockNav')
                                                            }
                                                        }
                                                    ]
                                                }
                                            ]
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                });


            this.hideAnimObj = new Anique()
                .add({
                    name: 'hide hands',
                    call: () => {
                        anime({
                            targets: '.section-3__hands',
                            translateY: '-100',
                            opacity: [1, 0],
                            easing: 'easeOutSine',
                            duration: 400,
                            elasticity: 0,
                            offset: 0
                        })
                    }
                })
                .add({
                    name: 'hide small line',
                    call: () => {
                        anime({
                            targets: '.section-3-small-spot__line',
                            scale: [1, 0],
                            rotate: [-45, -45],
                            duration: 400,
                            elasticity: 0,
                            easing: 'easeOutSine',
                            offset: 0
                        })
                    },
                    delay: 400,
                    next: [
                        {
                            name: 'hide holes',
                            call: () => {
                                this.$store.state.imageEffect.animateHoles(2, 'hide');
                            }
                        }, {
                            name: 'hide form bg',
                            call: () => {
                                anime({
                                    targets: '.section-3 .section-line-form',
                                    opacity: [1, 0],
                                    duration: 400,
                                    elasticity: 0,
                                    easing: 'easeOutSine',
                                    offset: 0
                                })
                            },
                            delay: 400,
                            next: [
                                {
                                    name: 'hide letters',
                                    call: () => {
                                        encryptLetters(title, 400);
                                    }
                                }, {
                                    name: 'hide text',
                                    call: () => {
                                        hideTextWithBg('.section-3');
                                    },
                                    delay: 800,
                                    next: [
                                        {
                                            name: 'move bg',
                                            call: () => {
                                                videoWrapper.style.display = "none";
                                                canvasWrapper.style.display = 'block';
                                                this.$store.state.zoomEffect.animate();
                                                let t3d = {
                                                    value: 0
                                                };
                                                let webgl = document.querySelector('.webgl-backgrounds');
                                                anime({
                                                    targets: t3d,
                                                    value: [0, 100],
                                                    duration: 1000,
                                                    elasticity: 0,
                                                    easing: [0.790, 0.010, 0.655, 0.990],
                                                    offset: 0,
                                                    update() {
                                                        webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                                                    }
                                                });
                                            },
                                            alternateCall: () => {
                                                this.$store.state.imageEffect.animateNext(2, this.$store.getters.getNextSlide-1, 2000);
                                            }
                                        }
                                    ]

                                }

                            ]
                        }, {
                            name: 'hide subtitles',
                            offset: 200,
                            call: () => {
                                anime({
                                    targets: '.section-3__subtitle',
                                    opacity: [1, 0],
                                    duration: 400,
                                    elasticity: 0,
                                    easing: 'easeOutSine'
                                })
                            }
                        }
                    ]
                });
        }
    }
</script>

<style lang="sass">
    @import "../../assets/sass/sections/section3.sass"
</style>
