        computed: {
            isBrifActive() {
              return this.$store.state.appState.brif;
            },
            demount(){
                return this.$store.state.appState.animationQueue.startDemountCurrentSlide
            },
            isFifthSlide() {
                return this.$store.state.appState.slides[4]
            },
            isRouteWipe() {
                return this.$store.state.startDestructionOnRouteLeave;
            },
            lang() {
                return this.$store.state.appState.language;
            }
        },
        watch: {
            demount(newValue, oldValue ) {
                if (oldValue != newValue && newValue == true && this.isFifthSlide) {
                    this.hideAnimObj.start(this.$store.getters.getMovingType);
                    this.hideAnimObj.destroy();
                    this.showAnimObj.destroy();
                    document.querySelector('#section5').classList.remove('section-5-optimize');
                    setTimeout(()=>this.$store.commit('toggleSlides',{fromId: 5, toId : this.$store.state.appState.animationQueue.nextSlide}),2600);
                }
            },
            isFifthSlide(newValue, oldValue){
                if (oldValue != newValue && newValue == true) {
                    document.querySelector('#section5').classList.add('section-5-optimize');
                    this.$store.state.imageEffect.fillCorrectors(4);
                    this.$store.state.imageEffect.fillPositions(4);
                    this.$store.state.imageEffect.renderSceneOnce(4);
                    this.showAnimObj.start(this.$store.getters.getMovingType);
                }
            },
            isRouteWipe(newValue, oldValue) {
                if (oldValue != newValue && newValue == true) {
                    this.hideAnimObj.destroy();
                    this.showAnimObj.destroy();
                }
            },
            lang(newValue, oldValue) {
                if (oldValue != newValue && this.isFifthSlide) {
                    this.errors = [];
                    let title = document.querySelectorAll('#section5 .lang-decrypt');
                    let static_translate = document.querySelectorAll('#section5 .lang-static');
                    setTimeout(()=>{
                        Array.from(title).forEach((el, i) => {
                            decryptLetters(el, 300)
                        });
                        Array.from(static_translate).forEach(el => {
                            staticTranslate(el)
                        });
                    }, 50)
                }
            }
        },
        methods:{
          enableBrif() {
              this.$store.commit('enableBrif');
              document.body.classList.add('--overflow-hidden');
          },
          disableBrif() {
              document.body.classList.remove('--overflow-hidden');
              this.$store.commit('disableBrif');
          },
          chechForm(){
            document.querySelector('.contact-data__email .form__input').addEventListener('input', (e) => {
             this.emailValidator = validateEmail(e.target.value)
             function validateEmail(email) {
                 var re = /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                 return re.test(String(email).toLowerCase());
              }
            });
             document.querySelector('.phone_validate .input').addEventListener('input', function (e) {
               var x = e.target.value.replace(/\D/g, '').match(/(\d{0,3})(\d{0,3})(\d{0,4})/);
               e.target.value = !x[2] ? x[1] : '(' + x[1] + ') ' + x[2] + (x[3] ? '-' + x[3] : '');
             });
          },
          submitForm(){
          this.errors = [];
            if (!this.form.info.name) {
              this.errors.push(this.locale[this.lang].buffer.inputErrName);
            }
            if (!this.form.info.phone) {
              this.errors.push(this.locale[this.lang].buffer.inputErrPhone);
            }
            if (this.emailValidator==false) {
              this.errors.push(this.locale[this.lang].buffer.inputErrMail);
            }
            if (this.form.info.name && this.emailValidator==true && this.form.info.phone) {
              var tl = new TimelineMax()
                  tl.fromTo(this.$refs.accepter,0.55,{y:40, opacity: 0, visibility: 'hidden'},{y:0,opacity: 1, visibility: 'visible'})
                  tl.to(this.$refs.accepter,0.35,{y:-40,opacity: 0},2.5)
                  tl.set(this.$refs.accepter,{visibility: 'hidden'})
              let fd = new FormData();
              fd.append('types', JSON.stringify(this.form.types) || 'не заполнено');
              fd.append('file', undefined);
              fd.append('budget', this.form.budget || 'не заполнено');
              fd.append('task', this.form.task || 'не заполнено');
              fd.append('name', this.form.info.name || 'не заполнено');
              fd.append('email', this.form.info.email || 'не заполнено');
              fd.append('phone', this.form.info.phone || 'не заполнено');
              fd.append('company', this.form.info.company || 'не заполнено');
                this.form.info.name = ''
                this.form.info.email = ''
                this.form.info.phone = ''
              axios.post('/api/mail', fd, {
                  headers: {
                      'Content-Type': 'multipart/form-data'
                  }
              })
            }
          }
        },
        mounted(){
          if(process.browser){
             this.chechForm()
           }
            let title = document.querySelector('.section-5__title');
            let videoWrapper = document.querySelector('.section__video-wrapper');
            let canvasWrapper = document.querySelector('.section__video-canvas');

            this.showAnimObj = new Anique()
                .add({
                    name: 'set events',
                    call: () => {
                        Array.from(document.querySelectorAll('.section-5 .form__label-text'))
                            .forEach(el => {
                                el.addEventListener('transitionend', onLabelTextTransitionEnd);
                            });
                    },
                    purge: () => {
                        Array.from(document.querySelectorAll('.section-5 .form__label-text'))
                            .forEach(el => {
                                el.removeEventListener('transitionend', onLabelTextTransitionEnd);
                            });
                    }
                })
                .add({
                    name: 'move bg',
                    call: () => {
                        let t3d = {
                            value: 0
                        };
                        let webgl = document.querySelector('.webgl-backgrounds');
                        anime({
                            targets: t3d,
                            value: [100, 0],
                            duration: 1000,
                            elasticity: 0,
                            easing: [0.790, 0.010, 0.655, 0.990],
                            offset: 0,
                            update() {
                                webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                            },
                            complete: () => {
                                videoWrapper.style.display = "block";
                                canvasWrapper.style.display = 'none';
                                this.$store.state.zoomEffect.stopAnimate();
                            }
                        });
                    },
                    alternateCall: () => {
                    },
                    delay: 1000,
                    next: [
                        {
                            name: 'show title',
                            call: () => {
                                decryptLetters(title, 800);
                            }
