                                                        this.$store.commit('unlockNav')
                                                        // document.querySelectorAll('.form__input-label').classList.remove('form__input-label--animate-hook');
                                                    }
                                                }
                                            ]
                                        }
                                    ]
                                }
                            ]

                        }
                    ]
                });

            this.hideAnimObj = new Anique()
                .add({
                    name: 'hide socials',
                    call: () => {
                        anime({
                            targets: '.socials__item',
                            scale: [1, 0],
                            rotate: [0, -45],
                            duration: 400,
                            easing: 'easeOutQuint',
                            offset: 0,
                            elasticity: 1000,
                            delay: function (el, i) {
                                return i * 100;
                            }
                        })
                    },
                    delay: 300,
                    next: [
                        {
                            name: 'hide holes',
                            call: ()=>{
                                this.$store.state.imageEffect.animateHoles(4, 'hide');
                            }
                        }, {
                            name: 'hide socials',
                            call: () => {
                                anime({
                                    targets: '.section-5 .button',
                                    opacity: [1, 0],
                                    duration: 300,
                                    easing: 'easeOutSine',
                                    elasticity: 0,
                                })
                            },
                            delay: 300,
                            next: [
                                {
                                    name: 'hide title',
                                    call: () => {
                                        anime({
                                            targets: '.section-5 .form__title',
                                            opacity: [1, 0],
                                            duration: 300,
                                            easing: 'easeOutSine',
                                            elasticity: 0
                                        })
                                    },
                                    delay: 350,
                                    next: [
                                        {
                                            name: 'encryptLetters',
                                            call: () => {
                                                encryptLetters(title, 400);
                                            }
                                        }, {
                                            name: 'hide bg',
                                            call: () =>{
                                                hideTextWithBg('.section-5__desc')
                                            }
                                        }, {
                                            name: 'hide form',
                                            offset: 850,
                                            call: () => {
                                                anime({
                                                    targets: '.section-5 .section-line-form',
                                                    opacity: [1, 0],
                                                    duration: 400,
                                                    elasticity: 0,
                                                    easing: 'easeOutSine',
                                                })
                                            },
                                            delay: 400,
                                            next: [
                                                {
                                                    name: 'bg move',
                                                    call: () => {
                                                        videoWrapper.style.display = "none";
                                                        canvasWrapper.style.display = 'block';
                                                        this.$store.state.zoomEffect.animate();
                                                        let t3d = {
                                                            value: 0
                                                        };
                                                        let webgl = document.querySelector('.webgl-backgrounds');
                                                        anime({
                                                            targets: t3d,
                                                            value: [0, 100],
                                                            duration: 1000,
                                                            elasticity: 0,
                                                            easing: [0.790, 0.010, 0.655, 0.990],
                                                            offset: 0,
                                                            update() {
                                                                webgl.style.transform = `translate3d(0,${t3d.value}%,0)`;
                                                            }
                                                        });
                                                    },
                                                    alternateCall: () => {
                                                        this.$store.state.imageEffect.animateNext(4, this.$store.getters.getNextSlide-1, 2000);
                                                    }
                                                }
                                            ]
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                })
                .add({
                    name: 'hide labels',
                    call: () => {
                        document.querySelector('.section-5__form').classList.remove('animate');

                        Array.from(document.querySelectorAll('.section-5 .form__label-text'))
                            .forEach(el => {
                                el.classList.remove('transition-end');
                            });
                    }
                })
                .add({
                    name: 'hide text',
                    call: () => {
                        hideTextWithBg('.section-5 .contacts');
                        this.disableBrif()
                    }
                });
        }
    }
</script>

